import React from 'react';
import { useState } from 'react/cjs/react.production.min';

import './Main.scss';

export default function Main({ viewDataWeatherCity, dataWeatherCity }) {
    // для иконки openweathermap.org/img/wn/${data.weather.icon}@2x.png путь

    const [temp, setTemp] = useState(false);


    React.us

    return (
        <main className="Main">
            { viewDataWeatherCity ? 
                <div className="Main__data">
                    <div className='Main__wrapper'>
                        {temp ? <img src={`http://openweathermap.org/img/wn/${dataWeatherCity.weather[0].icon}@4x.png`} alt="http://openweathermap.org/img/wn/$@2x.png"/> : <p>загрузка</p>}
                        <div className="Main__data-temper">{parseInt(dataWeatherCity.main.temp)}&deg;</div>
                    </div>
                    <div className="Main__data-description">{dataWeatherCity.weather[0].description}</div>       
                </div>
                :
                <div>выбери город</div>
            }
        </main>
    );
}
