// для градуса &deg;
// для иконки openweathermap.org/img/wn/${data.weather.icon}@2x.png путь 
// сделать крестик
// закрытие блока поиска при клике на другую область 
// разобраться с местоположением
// вывести погоду и все данные 

import React from 'react';
import axios from 'axios';
import DATA_CITIES from './arrayItemsCities.json'

import './App.scss';



function App() {
  const [russianCitiesData, setRussianCitiesData] = React.useState([]); 
  const [dataWeatherCity, setDataWeatherCity] = React.useState({}); 
  const [viewDataWeatherCity, setViewDataWeatherCity] = React.useState(false); 
  const [displaySearchResults, setDisplaySearchResults] = React.useState(false);
  const [cityName, setCityName] = React.useState('');
  const [searchValue, setSearchValue] = React.useState(''); 

  const filtredItemsArrayCities = DATA_CITIES.filter(item => item.hasOwnProperty('city'));

  const filterCity = (searchText, listOfCity) => {
    if (!searchText) {
      setDisplaySearchResults(false);
      return listOfCity;
    }

    return listOfCity.filter(({ city }) => city.toLowerCase().includes(searchText.toLowerCase()));
  }

  React.useEffect(() => {
    const debounce = setTimeout(() => {
      const filtredCity = filterCity(searchValue, filtredItemsArrayCities);
      setRussianCitiesData(filtredCity);
    }, 300);

    return () => clearTimeout(debounce)
  }, [searchValue]);

  const generatingObjectWithWeatherData = (objData) => {
    // console.log(objData);
    // if (objData.length) {
    //   for(key in objData) {
    //     console.log(key);
    //   }
    // }
  };

  const getDataWeather = (arrGeoPositionData) => {
    try {
      axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${arrGeoPositionData[0]}&lon=${arrGeoPositionData[1]}&units=metric&appid=${process.env.REACT_APP_OPENWEATHER_KEY}`).then(res => {
        setDataWeatherCity(res.data); // передача в хук объекта api
        setViewDataWeatherCity(res.data);
      });
    } catch(error) {
      console.log(error);
    }
  }

  // обработчик клика на элемент списка
  const handlerClickOnCity = (e) => {
    let cityName = e.target.textContent;

    setCityName(cityName);

    filtredItemsArrayCities.forEach((item, index) => {
      if (cityName === item.city) {
        getDataWeather([item.geo_lat, item.geo_lon]);
      }
    }) 

    setSearchValue(cityName);
    setDisplaySearchResults(false);
    // setCityName(cityName);
  }

  const onChangeSearchInput = (e) => {
    setSearchValue(e.target.value);
    setDisplaySearchResults(true);
  }



  return (
    <div className="App">
      <header className="App-header">
        <div className="App-header__searchBar">
          {
            cityName ? 
              <div>
                <div className="App-header__cityName">{cityName}</div>
                <div>
                  <div className="App-header__changeCity">Сменить город</div>
                  <div className="App-header__location">Моё местоположение</div>
                </div>
              </div>
            : 
              <div className="App-header__wrapper">
            <input onChange={onChangeSearchInput} className="App-header__searchInput" value={searchValue} type="text" placeholder="введите город..."/> 
            <ul className={"_searchResults " + (displaySearchResults && "App-header__visible")}>
              { 
              russianCitiesData.length ? russianCitiesData.slice(0, 5).map((dataCity, index) => {
                return <li key={index}className="App-header__searchResults-item" onClick={handlerClickOnCity}>{dataCity.city}</li>
              }) : <li>{'город не найден...'}</li> 
              } 
            </ul>
          </div>
          }


        </div>

      </header>

      <main className="App-main">
        <div className='App-main__wrapper'>
          <img src='http://openweathermap.org/img/wn/02d@2x.png' alt="http://openweathermap.org/img/wn/02d@2x.png"/>
          <div className='App-main__temper'>
            { viewDataWeatherCity ? 
                <div>dataWeatherCity.main.temp</div>
                :
                console.log('hyi')
            }
            {/* 19&deg; */}
          </div>
        </div>
        <div>Преимущественно солнечно</div>

      </main>

      <footer className="App-footer">
        <div className="App-footer__wind">
          <div>Ветер</div>
          <div>5 м/с, заподный</div>
        </div>
        <div className="App-footer__pressured">
          <div>Давление</div>
          <div>752 мм рт.ст.</div>
        </div>
        <div className="App-footer__humidity">
          <div>Влажность</div>
          <div>60%</div>
        </div>
        <div className="App-footer__rain">
          <div>Вероятность дождя</div>
          <div>10%</div>
        </div>
      </footer>
    </div>
  );
}

export default App;
