import React from 'react';

import './Main.scss';

export default function Main({ viewDataWeatherCity, dataWeatherCity, celsius = false, fahrenheit = false}) {

    const [fahrenheitValue, setFahrenheitValue] = React.useState(!fahrenheit);
    // для иконки openweathermap.org/img/wn/${data.weather.icon}@2x.png 

    const [ link, setLink ]= React.useState('');

    return (
        <main className="Main">
            { viewDataWeatherCity ? 
                <div className="Main__data">
                    <div className='Main__wrapper'>
                        <img src={`http://openweathermap.org/img/wn/${dataWeatherCity.weather[0].icon}@4x.png`} alt="img"/>
                        { fahrenheit ? <div className="Main__data-temper">{parseInt(+fahrenheitValue)}</div> : <div className="Main__data-temper">{parseInt(dataWeatherCity.main.temp)}&deg;</div>}
                    </div>
                    <div className="Main__data-description">{dataWeatherCity.weather[0].description}</div>       
                </div>
                :
                <div>необходимо выбрать город</div>
            }
        </main>
    );
}
