  const searchItem = (filtredItemsArrayCities, searchValue) => {
    if (searchValue.length === 0) return filtredItemsArrayCities;

    return filtredItemsArrayCities.filter(({ city }) => {
      return city.toLowerCase().indexOf(searchValue.toLowerCase()) > -1;
    })
  }

  const visibleItem = searchItem(filtredItemsArrayCities, searchValue).map(({ city }) => {
    console.log(city);
    return ( 
      <li>{ city }</li> 
    )
  })  




  // для градуса &deg;
  // для иконки openweathermap.org/img/wn/${data.weather.icon}@2x.png путь 

