  //   } catch(error) {
  //     console.log(error);
  //   }
  // }, [])

  const onChangeSearchInput = (e) => {
    setSearchValue(e.target.value)
  }


  return (
    <div className="App">
      <header className="App-header">
        <div className="searchBar">
          <input onChange={onChangeSearchInput} className="searchInput" value={searchValue} type="text" placeholder="введите город..."/> 
          <ul className={"searchResults " + (searchValue !== '' ? "visible" : null)}>
            { searchValue && visibleItem } 
            {/* { 
            data.length ? data.map((dataCity, index) => {
              return <li key={index}>{dataCity.city}</li>
            }) : <li>{'город не найден...'}</li> 
            }  */}
          </ul>

        </div>

      </header>
    </div>
  );
}

export default App;
