import React from 'react';

import {TemperContext} from '../../App';

import './Header.scss';

export default function Header({ 
    cityName, 
    searchValue, 
    onChangeSearchInput, 
    displaySearchResults, 
    russianCitiesData, 
    handlerClickOnCity, 
    setSearchValue,
    changeCity,
    toggleTempFormatHandler
    // handlerCelsius,
    // habdlerFahrenheit
}) {

    // const [isHandelrCelsius, setIsHandelrCelsius] = React.useState(true);
    // const [isHandelrFahrenheit, setIsHandelrFahrenheit] = React.useState(false);

    // const onClickCelsius = () => {
    //     setIsHandelrCelsius(!isHandelrCelsius);
    //     setIsHandelrFahrenheit(!isHandelrFahrenheit);
    //     // handlerCelsius(isHandelrCelsius);
    //     console.log('header:','fahrenheit:',isHandelrFahrenheit,'celsius:', isHandelrCelsius);
    // }

    // const onClickFahrenheit = () => {
    //     setIsHandelrFahrenheit(!isHandelrFahrenheit); 
    //     setIsHandelrCelsius(!isHandelrCelsius);
    //     // habdlerFahrenheit(isHandelrFahrenheit);
    //     console.log('header:','fahrenheit:',isHandelrFahrenheit,'celsius:', isHandelrCelsius);
    // }

    // React.useEffect(() => {
    //     habdlerFahrenheit(isHandelrFahrenheit);
    // }, [isHandelrFahrenheit]);
    // React.useEffect(() => {
    //     handlerCelsius(isHandelrCelsius);
    // }, [isHandelrCelsius]);


    return (
        <header className="Header">
            <div className="Header__searchBar">
            {
                cityName ? 
                    <div className="Header__searchBar__geoPosition">
                        <div className="Header__searchBar__geoPosition-сityName">
                            {cityName}
                            <div className="Header__searchBar__geoPosition-blockButton">
                                {/* <span>&deg;</span>
                                <div onClick={onClickCelsius}>C</div>
                                <div onClick={onClickFahrenheit}>F</div> */}
                                <span>&deg;</span>
                                <div onClick={toggleTempFormatHandler}>C</div>
                                <div onClick={toggleTempFormatHandler}>F</div>

                            </div>    
                        </div>
                        <div className="Header__searchBar__geoPosition-wrapper">
                            <div className="Header__searchBar__geoPosition-changeCity" onClick={changeCity}>сменить город</div>
                            <img src="/image/location.png" alt="location" />
                            <div className="Header__searchBar__geoPosition-location">Моё местоположение</div>
                        </div>
                    </div>
                : 
                <div className="Header__wrapper">
                    {searchValue && <img onClick={() => setSearchValue('')} className="clear" src="/image/btn-remove.svg" alt="remove"/>}
                    <input onChange={onChangeSearchInput} className="Header__searchBar-searchInput" value={searchValue} type="text" placeholder="введите город..."/> 
                    <ul className={"_searchResults " + (displaySearchResults && "Header__visible")}>
                        { 
                            russianCitiesData.length ? russianCitiesData.slice(0, 5).map((dataCity, index) => {
                                return <li key={index} className="Header__searchResults-item" onClick={handlerClickOnCity}>{dataCity.city}</li>
                            }) : <li>{'город не найден...'}</li> 
                        } 
                    </ul>
                </div>
            }
            </div>
        </header>
    );
}
