import React from 'react';

import './Footer.scss';

export default function Footer({ viewDataWeatherCity = false, dataWeatherCity }) {

    const [directionWind, setDirectionWind] = React.useState('');



   // NE — северо-восток 11.25 - 56.25
   // SE — юго-восток 101.25 - 146.25
    // E - восток 78.75 - 101.25
   // N - север 348.75 - 11.25
    // SW — юго-запад 191.25 -  236.25
    // S - юг 168.75 - 191.25
    // NW — северо-запад 281.25 - 348.75
   // W - запад 258.75 - 281.25




    // React.useEffect(() => {
        // function directionDetermination(temp) {
        //     if ( Number(temp) >= 191.25  && Number(temp) <= 236.25 ) setDirectionWind('юго-заподный');

        if ( Number(temp) >= 348.75  && Number(temp) <= 11.25 ) setDirectionWind('северный');

        if ( Number(temp) >= 11.25 && Number(temp) <= 78.75 ) setDirectionWind('северо-восточный');


        if ( Number(temp) >= 78.75  && Number(temp) <= 101.25 ) setDirectionWind('восточный');

        if ( Number(temp) >= 168.75 && Number(temp) <= 191.25 ) setDirectionWind('южный');


        if ( Number(temp) >= 281.25  && Number(temp) <= 348.75 ) setDirectionWind('северо-западный');

        if ( Number(temp) >= 258.75  && Number(temp) <= 281.25 ) setDirectionWind('западный');

        if ( Number(temp) >= 101.25  && Number(temp) <= 146.25 ) setDirectionWind('юго-восточный');

        // }


    // }, [see]);



    if(viewDataWeatherCity === true) {
        directionDetermination(dataWeatherCity.wind.deg);
        // setAsd(dataWeatherCity.wind.deg);


    }
    // console.log( see, asd);
    return (
        <footer className="App-footer">
            <div className="App-footer__wind">
                <div>Ветер</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.wind.gust} м/с, {console.log(directionWind)}</div> : <></>}
            </div>
            <div className="App-footer__pressured">
                <div>Давление</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.grnd_level} мм рт.ст.</div> : <></>}
            </div>
            <div className="App-footer__humidity">
                <div>Влажность</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.humidity}%</div> : <></>}
            </div>
            <div className="App-footer__rain">
                <div>Вероятность дождя</div>
                <div>10%</div>
            </div>
      </footer>
    );
}
