    onst filtredItemsArrayCities = DATA_CITIES.filter(item => item.hasOwnProperty('city'));

  const searchItem = (filtredItemsArrayCities, searchValue) => {
    if (searchValue.length === 0) return filtredItemsArrayCities;

    return filtredItemsArrayCities.filter(({ city }) => {
      return city.toLowerCase().indexOf(searchValue.toLowerCase()) > -1;
    })
  }

  const visibleItem = searchItem(filtredItemsArrayCities, searchValue).map(({ city }) => {
    return ( 
      <li>{ city }</li> 
    )
  })  
  */



  // для градуса &deg;
  // для иконки openweathermap.org/img/wn/${data.weather.icon}@2x.png путь 

  React.useEffect(() => {
    try {
      axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=54.983334&lon=73.366669&units=metric&appid=${process.env.REACT_APP_OPENWEATHER_KEY}`).then(res => setData(res.data));
    } catch(error) {
      console.log(error);
    }
  }, [])

  const onChangeSearchInput = (e) => {
    setSearchValue(e.target.value)
  }


  return (
    <div className="App">
      <header className="App-header">
        <div className="searchBar">
          <input onChange={onChangeSearchInput} className="searchInput" value={searchValue} type="text" placeholder="введите город..."/> 
          <ul className={"searchResults " + (searchValue !== '' ? "visible" : null)}>
            { searchValue && visibleItem } 
          </ul>

        </div>

      </header>
    </div>
  );
}

export default App;
