import React from 'react';

import './Footer.scss';

export default function Footer({ viewDataWeatherCity, dataWeatherCity }) {

    let directionWind;

   // NE — северо-восток 11.25 - 56.25
   // SE — юго-восток 101.25 - 146.25
    // E - восток 78.75 - 101.25
   // N - север 348.75 - 11.25
    // SW — юго-запад 191.25 -  236.25
    // S - юг 168.75 - 191.25
    // NW — северо-запад 281.25 - 348.75
   // W - запад 258.75 - 281.25

    if(viewDataWeatherCity) {
        let temp = Number(dataWeatherCity.wind.deg);

        switch(temp){
            case temp => 11.25 && temp <= 56.25:
                directionWind = 'северо-восточный';
                break;
            case temp => 348.75  && temp <= 11.25:
                directionWind = 'северный';
                break;
            case temp => 191.25  && temp <= 236.25:
                directionWind = 'юго-заподный';
                break;
            case temp => 78.75  && temp <= 101.25:
                directionWind = 'восточный';
                break;
            case temp => 168.75 && temp <= 191.25:
                directionWind = 'южный';
                break;
            case temp => 101.25  && temp <= 146.25:
                directionWind = 'юго-восточный';
                break;
            case temp => 281.25  && temp <= 348.75:
                directionWind = 'северо-западный';
                break;
            case temp => 258.75  && temp <= 281.25:
                directionWind = 'западный';
                break;
        }

    }

    console.log(directionWind);

    return (
        <footer className="App-footer">
            <div className="App-footer__wind">
                <div>Ветер</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.wind.gust} м/с, {directionWind}</div> : <></>}
            </div>
            <div className="App-footer__pressured">
                <div>Давление</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.grnd_level} мм рт.ст.</div> : <></>}
            </div>
            <div className="App-footer__humidity">
                <div>Влажность</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.humidity}%</div> : <></>}
            </div>
            <div className="App-footer__rain">
                <div>Вероятность дождя</div>
                <div>10%</div>
            </div>
      </footer>
    );
}
