      // // console.log(temp);


      // if (!temp == 'false') {
      //   console.log('false');
      // } else {
      //   console.log('true');
      // }
    // })

    return filtredItemsArrayCities.filter(cityData => {
      return cityData.city.toLowerCase().indexOf(searchValue.toLowerCase()) > -1;
    })
  }

  const visibleItem = searchItem(filtredItemsArrayCities, searchValue).map(dataCity => {
    return (
      <li>{ dataCity.city }</li>
    )
  })  

  // для градуса &deg;
  // для иконки openweathermap.org/img/wn/${data.weather.icon}@2x.png путь 

  React.useEffect(() => {
    try {
      axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=54.983334&lon=73.366669&units=metric&appid=${process.env.REACT_APP_OPENWEATHER_KEY}`).then(res => setData(res.data));
    } catch(error) {
      console.log(error);
    }
  }, [])

  const onChangeSearchInput = (e) => {
    setSearchValue(e.target.value)
  }


  return (
    <div className="App">
