import React from 'react';

import './Footer.scss';

export default function Footer({ viewDataWeatherCity, dataWeatherCity }) {

    let directionWind;

    // NNE 11.25 - 33.75
    // N 348.75 - 11.25
    // NE 33.75 - 56.25
    // ENE 56.25 - 78.75

    E

78.75 - 101.25

ESE

101.25 - 123.75

    if(viewDataWeatherCity) {
        let temp = Number(dataWeatherCity.wind.deg);

        switch(temp){
            case temp => 11.25 && temp <= 33.75:
                directionWind = 'северо-северо-восток';
            case temp => 348.75  && temp <= 11.25:
                directionWind = 'север';
            case temp => 33.75  && temp <= 56.25:
                directionWind = 'северо-восток';
            case temp => 56.25  && temp <= 78.75:
                directionWind = ' востоко-северо-восток';
            case temp => 56.25  && temp <= 78.75:
                directionWind = ' востоко-северо-восток';
        }

    }

    return (
        <footer className="App-footer">
            <div className="App-footer__wind">
                <div>Ветер</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.wind.gust} м/с, {dataWeatherCity.wind.deg}</div> : <></>}
            </div>
            <div className="App-footer__pressured">
                <div>Давление</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.grnd_level} мм рт.ст.</div> : <></>}
            </div>
            <div className="App-footer__humidity">
                <div>Влажность</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.humidity}%</div> : <></>}
            </div>
            <div className="App-footer__rain">
                <div>Вероятность дождя</div>
                <div>10%</div>
            </div>
      </footer>
    );
}
