import React from 'react';

import './Footer.scss';

export default function Footer({ viewDataWeatherCity, dataWeatherCity }) {

    const [direction, setDirection] = React.useState(dataWeatherCity.wind.deg);


   // NE — северо-восток 11.25 - 56.25
   // SE — юго-восток 101.25 - 146.25
    // E - восток 78.75 - 101.25
   // N - север 348.75 - 11.25
    // SW — юго-запад 191.25 -  236.25
    // S - юг 168.75 - 191.25
    // NW — северо-запад 281.25 - 348.75
   // W - запад 258.75 - 281.25



   var directionWind;

    React.useEffect(() => {
        // function directionDetermination(dir) {
        //     let temp = Number(dir)
            if ( direction => 191.25  && direction <= 236.25 ) return directionWind = 'юго-заподный';
            // if ( temp => 348.75  && temp <= 11.25 ) return directionWind = 'северный';
            // if ( temp => 11.25 && temp <= 56.25 ) return directionWind = 'северо-восточный';

            // if ( temp => 78.75  && temp <= 101.25 ) return directionWind = 'восточный';
            // if ( temp => 168.75 && temp <= 191.25 ) return directionWind = 'южный';

            // if ( temp => 281.25  && temp <= 348.75 ) return directionWind = 'северо-западный';
            // if ( temp => 258.75  && temp <= 281.25 ) return directionWind = 'западный';
            // if ( temp => 101.25  && temp <= 146.25 ) return directionWind = 'юго-восточный';
        // }

        // directionDetermination(direction)
    }, [direction])


    if(viewDataWeatherCity) {
        let direction = dataWeatherCity.wind.deg;
        setDirection(direction);
    }



    console.log(directionWind, direction);

    return (
        <footer className="App-footer">
            <div className="App-footer__wind">
                <div>Ветер</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.wind.gust} м/с, {directionWind}</div> : <></>}
            </div>
            <div className="App-footer__pressured">
                <div>Давление</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.grnd_level} мм рт.ст.</div> : <></>}
            </div>
            <div className="App-footer__humidity">
                <div>Влажность</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.humidity}%</div> : <></>}
            </div>
            <div className="App-footer__rain">
                <div>Вероятность дождя</div>
                <div>10%</div>
            </div>
      </footer>
    );
}
