import React from 'react';
import { useEffect } from 'react/cjs/react.production.min';

import './Footer.scss';

export default function Footer({ viewDataWeatherCity, dataWeatherCity }) {

    const [direction, setDirection] = React.useState('');


   // NE — северо-восток 11.25 - 56.25
   // SE — юго-восток 101.25 - 146.25
    // E - восток 78.75 - 101.25
   // N - север 348.75 - 11.25
    // SW — юго-запад 191.25 -  236.25
    // S - юг 168.75 - 191.25
    // NW — северо-запад 281.25 - 348.75
   // W - запад 258.75 - 281.25



    let directionWind;
    useEffect

    if(viewDataWeatherCity) {
        let direction = Number(dataWeatherCity.wind.deg);
        setDirection(direction);
    }



    console.log(directionWind, direction);

    return (
        <footer className="App-footer">
            <div className="App-footer__wind">
                <div>Ветер</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.wind.gust} м/с, {directionWind}</div> : <></>}
            </div>
            <div className="App-footer__pressured">
                <div>Давление</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.grnd_level} мм рт.ст.</div> : <></>}
            </div>
            <div className="App-footer__humidity">
                <div>Влажность</div>
                {viewDataWeatherCity ? <div>{dataWeatherCity.main.humidity}%</div> : <></>}
            </div>
            <div className="App-footer__rain">
                <div>Вероятность дождя</div>
                <div>10%</div>
            </div>
      </footer>
    );
}
