import React from 'react';

import './Header.scss';

export default function Header({ 
    cityName, 
    searchValue, 
    onChangeSearchInput, 
    displaySearchResults, 
    russianCitiesData, 
    handlerClickOnCity, 
    changeCity 
}) {

    return (
        <header className="Header">
            <div className="Header__searchBar">
            {
                cityName ? 
                <div>
                    <div className="Header__cityName">{cityName}</div>
                    <div>
                    <div className="Header__changeCity" onClick={changeCity}>Сменить город</div>
                    <div className="Header__location">Моё местоположение</div>
                    </div>
                </div>
                : 
                <div className="Header__wrapper">
                <input onChange={onChangeSearchInput} className="Header__searchInput" value={searchValue} type="text" placeholder="введите город..."/> 
                <ul className={"_searchResults " + (displaySearchResults && "Header__visible")}>
                { 
                russianCitiesData.length ? russianCitiesData.slice(0, 5).map((dataCity, index) => {
                    return <li key={index} className="Header__searchResults-item" onClick={handlerClickOnCity}>{dataCity.city}</li>
                }) : <li>{'город не найден...'}</li> 
                } 
                </ul>
                </div>
            }
            </div>
        </header>
    );
}
